--Client table creation statement

CREATE TABLE CLIENT
  (
    client_id    NUMBER PRIMARY KEY,
    client_name  VARCHAR2(50)NOT NULL,
    street_name  VARCHAR2(50),
    city         VARCHAR2(20),
    State        VARCHAR2(20),
    zipcode      NUMBER NOT NULL,
    contact_name VARCHAR2(50)NOT NULL,
    phone_number VARCHAR2(10) NOT NULL
  );
 
--Product configuration table creation statement


CREATE TABLE PRODUCT_CONFIG
  (
    model_id       NUMBER PRIMARY KEY,
    model_name     VARCHAR2(20)NOT NULL,
    model_Category VARCHAR2(20) NOT NULL
  );

-- Project_Employee table creation statement

CREATE TABLE PROJECT_EMPLOYEE
(
EMPLOYEE_ID NUMBER PRIMARY KEY,
FIRST_NAME VARCHAR2(50) NOT NULL,
LAST_NAME VARCHAR2(50) NOT NULL,
EMAIL VARCHAR2(50) NOT NULL UNIQUE,
HIRE_DATE DATE NOT NULL,
JOB_ID VARCHAR2(20) NOT NULL,
PHONE_NUMBER NUMBER
);

-- Projects table creation statement

CREATE TABLE PROJECT
(
PROJECT_ID NUMBER PRIMARY KEY,
CLIENT_ID NUMBER NOT NULL,
START_DATE DATE NOT NULL,
END_DATE DATE,
DEADLINE DATE NOT NULL,
SALESREP_ID NUMBER,
FIS_ID NUMBER,
STATUS VARCHAR2(50),
AGREED_BUDGET NUMBER NOT NULL,
ACTUAL_BUDGET NUMBER,
CONSTRAINT fk_client
    FOREIGN KEY (client_id)
    REFERENCES client(client_id),
CONSTRAINT fk_sales_employee
    FOREIGN KEY (SALESREP_ID)
    REFERENCES PROJECT_EMPLOYEE(EMPLOYEE_ID),
CONSTRAINT fk_fis_employee
    FOREIGN KEY (FIS_ID)
    REFERENCES PROJECT_EMPLOYEE(EMPLOYEE_ID)
);

-- Project_Details table creation statement

CREATE TABLE PROJECT_DETAILS 
(
PROJECT_ID NUMBER,
MODEL_ID NUMBER,
PRIORITY NUMBER NOT NULL,
QUANTITY NUMBER NOT NULL,
STATUS VARCHAR2(20),
MODEL_CATEGORY VARCHAR2(1) NOT NULL,
CAPACITY  NUMBER NOT NULL,
PURPOSE VARCHAR2(20) NOT NULL,
CONSTRAINT pk_project_details
    PRIMARY KEY(PROJECT_ID,MODEL_ID),
CONSTRAINT fk_project_id
    FOREIGN KEY (PROJECT_ID)
    REFERENCES PROJECT(PROJECT_ID),
CONSTRAINT fk_model_id
    FOREIGN KEY (MODEL_ID)
    REFERENCES PRODUCT_CONFIG(MODEL_ID)
);


-- Project_Details_Escalator details table creation statement

CREATE TABLE PROJECT_DETAILS_ESCALATOR 
(
PROJECT_ID NUMBER,
MODEL_ID NUMBER,
VELOCITY NUMBER NOT NULL,
ANGLE NUMBER NOT NULL,
STEP_WIDTH NUMBER NOT NULL,
CONSTRAINT pk_project_det_escalator
    PRIMARY KEY(PROJECT_ID,MODEL_ID),
CONSTRAINT fk_project_det_esc
    FOREIGN KEY (PROJECT_ID,MODEL_ID)
    REFERENCES PROJECT_DETAILS(PROJECT_ID,MODEL_ID)
);

-- Project_Details_Elevator details table creation statement

CREATE TABLE PROJECT_DETAILS_ELEVATOR
(
PROJECT_ID NUMBER,
MODEL_ID NUMBER,
LENGTH NUMBER NOT NULL,
BREADTH NUMBER NOT NULL,
HEIGHT NUMBER NOT NULL,
DOOR_TYPE VARCHAR2(15) NOT NULL,
MAX_RISE NUMBER NOT NULL,
MAX_SPEED NUMBER NOT NULL,
CONSTRAINT pk_project_det_elevator
    PRIMARY KEY(PROJECT_ID,MODEL_ID),
CONSTRAINT fk_project_det_ele
    FOREIGN KEY (PROJECT_ID,MODEL_ID)
    REFERENCES PROJECT_DETAILS(PROJECT_ID,MODEL_ID)
);

